import React, { Component } from 'react';

class Game extends Component{
constructor(props){
    super(props);
    this.state ={ items: [],
            isLoaded: false,
        }
    }
    componentDidMount(){
        fetch('http://jservice.io/api/random')
        .then(res => res.json())
        .then(json => {
            this.setState({
                isLoaded:true,
                items:json,
            })
        })
    }
    render(){
        var {items, isLoaded} =this.state
        if(!isLoaded){
            return <div>Loading </div>
        }
        return(
            <div className='Game'>
            </div>
        )
    }
}
export default Game
