import React, { Component } from 'react';

class Game extends Component {
	constructor(props) {
		super(props);
		this.state = { items: [], isLoaded: true, isToggleOn: false, score: 0};
		this.toggle = this.toggle.bind(this);
	}
	componentDidMount() {
		fetch('http://jservice.io/api/random')
			.then((res) => res.json())
			.then((json) => {
				this.setState({
					isLoaded: true,
					items: json,
                    isToggleOn: true
				});
			});
	}

	toggle(e){
		this.setState((state) => ({
			isToggleOn: !state.isToggleOn,
		}));
	}

	addPoints = (e) => {
        this.setState((state) => ({
            score: state.score += e

        }))
        fetch('http://jservice.io/api/random')
			.then((res) => res.json())
			.then((json) => {
				this.setState({
					isLoaded: true,
					items: json,
                    isToggleOn: true
				});
			});
    };

    reset = ()=> {
        this.setState((state) => ({
					score: state.score = 0
				}));

    }



	render() {
		const { items, isLoaded } = this.state;
		if (!isLoaded) {
			return <div>Loading ...</div>;
		} else {
			return (
				<div className='Game'>
					<h3>Question:</h3>{' '}
					<div style={{ listStyleTydive: 'none' }}>
						{items.map((item) => (
							<p key={item.id}>{item.question}</p>
						))}
					</div>
					<div>
						<h3>Catagory:</h3>{' '}
						{items.map((item) => (
							<p key={item.id}> {item.category.title}</p>
						))}
					</div>
					<div>
						<h3>Answer:</h3>

							{this.state.isToggleOn ? '' : this.state.items.map((item) => (
							<p key={item.id}> {item.answer}</p>
						))}

						<button onClick={this.toggle}>Click for answer

						</button>
					</div>
					<div>
						<h3>Points:</h3>{' '}
						{items.map((item) => (
							<p key={item.id}> {item.value}</p>
						))}
					</div>
					<div>
						<h2>Score: {this.state.score} </h2>
						<button onClick={() => this.addPoints(this.state.items[0].value)}>add points </button>
						<button onClick={() => this.addPoints(-this.state.items[0].value)} >subtract points</button>
					</div>
				</div>
			);
		}
	}
}
export default Game;
