import React, { Component } from 'react';

class Game extends Component {
	constructor(props) {
		super(props);
		this.state = { items: [], isLoaded: true, isToggleOn: false };
		this.toggle = this.toggle.bind(this);
	}
	componentDidMount() {
		fetch('http://jservice.io/api/random')
			.then((res) => res.json())
			.then((json) => {
				this.setState({
					isLoaded: true,
					items: json,
                    isToggleOn: true
				});
			});
	}

	toggle(e){
		this.setState((state) => ({
			isToggleOn: !state.isToggleOn,
		}));
	}

	addPoints = (e) => {};

	subtractPoints;

	render() {
		const { items, isLoaded } = this.state;
		if (!isLoaded) {
			return <div>Loading ...</div>;
		} else {
			return (
				<div className='Game'>
					<h3>Question:</h3>{' '}
					<div style={{ listStyleTydive: 'none' }}>
						{items.map((item) => (
							<p key={item.id}>{item.question}</p>
						))}
					</div>
					<div>
						<h3>Catagory:</h3>{' '}
						{items.map((item) => (
							<p key={item.id}> {item.category.title}</p>
						))}
					</div>
					<div>
						<h3>Answer:</h3>

