import React, { Component } from 'react';

class Game extends Component{
constructor(props){
    super(props);
    this.state ={ items: [],
            isLoaded: true,
            active: true
        }
    }
    componentDidMount(){
        fetch('http://jservice.io/api/random')
        .then(res => res.json())
        .then(json => {
            this.setState({
                isLoaded:true,
                items:json,
            })
        })
    }

    toggle= (e) => {
const currentState = this.state.active
this.setState({active: !currentState})
    }

    render(){
        const {items, isLoaded} =this.state
        if(!isLoaded){
            return <div>Loading ...</div>
        } else {


        return (
					<div className='Game'>
						<h3>Question:</h3>{' '}
						<div style={{ listStyleTydive: 'none' }}>
							{items.map((item) => (
								<p key={item.id}>{item.question}</p>
							))}
						</div>
						<div>
							<h3>Catagory:</h3>{' '}
							{items.map((item) => (
								<p key={item.id}> {item.category.title}</p>
							))}
						</div>



						<div ClassName={this.state.active ? ClassName= {div}: null} 
                        onClick={this.toggle}>
							<h3>Answer:</h3>{' '}
							{items.filter((item) => item.show === false).map((item) => ( 
								<p key={item.id}> {item.answer}</p>
							))}
                            {/* <button onClick={(e) => this.setState({show:false})></button> */}
						</div>





						<div>
							<h3>Points:</h3>{' '}
							{items.map((item) => (
								<p key={item.id}> {item.value}</p>
							))}
						</div>
					</div>
				);
        }
    }
}
export default Game
